import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function ProductoDetalles() {
  const { id } = useParams();
  const [producto, setProducto] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3001/producto/${id}`)
      .then(res => res.json())
      .then(data => setProducto(data))
      .catch(err => console.error(err));
  }, [id]);

  if (!producto) return <p>Cargando...</p>;

  return (
    <div style={{ padding: "30px" }}>
      <div style={{
        display: "flex",
        gap: "40px",
        alignItems: "flex-start",
        marginBottom: "50px"
      }}>
        <div>
          <img 
            src={producto.imagen}
            alt={producto.nombre_producto}
            style={{ width: "350px", borderRadius: "10px" }}
          />
        </div>
        <div style={{ flex: 1 }}>
          <h1>{producto.nombre_producto}</h1>

          <p><strong>Precio:</strong> S/. {producto.precio}</p>
          <p><strong>Descripción:</strong> {producto.caracteristica}</p>
          <p><strong>Categoría:</strong> {producto.categoria}</p>
          <p><strong>Competencia:</strong> {producto.competencia}</p>
          <p><strong>Marca:</strong> {producto.marca}</p>
          <p><strong>Equipo:</strong> {producto.equipo}</p>

          <button
            onClick={agregarAlCarrito}
            style={{
              marginTop: "20px",
              padding: "12px 20px",
              backgroundColor: "#2b7cff",
              color: "white",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
              fontSize: "16px"
            }}
          >
            Agregar al carrito
          </button>
        </div>
      </div>

      <div style={{
        borderTop: "1px solid #ddd",
        paddingTop: "20px"
      }}>
        <h2>Reseñas</h2>
        <p>Aquí irán las reseñas del producto...</p>
      </div>

    </div>
  );
}
