import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Carrito() {
  const [items, setItems] = useState([]);
  const id_cliente = localStorage.getItem("id_usuario");

  const cargarCarrito = () => {
    fetch(`http://localhost:3001/carrito?id_cliente=${id_cliente}`)
      .then(res => res.json())
      .then(data => {
        if (data.mensaje) setItems([]); // osea no hay nada en el carrito
        else setItems(data);
      });
  };

  useEffect(() => {
    cargarCarrito();
  }, []);

  const actualizarCantidad = (id_producto, nuevaCantidad) => {
    fetch("http://localhost:3001/carrito/actualizar", {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        id_cliente,
        id_producto,
        cantidad: nuevaCantidad
      })
    })
      .then(res => res.json())
      .then(() => cargarCarrito());
  };

  const eliminarItem = (id_producto) => {
    fetch("http://localhost:3001/carrito/eliminar-item", {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ id_cliente, id_producto })
    })
      .then(res => res.json())
      .then(() => cargarCarrito());
  };

  return (
    <div style={{ padding: "30px" }}>
      <h1>Mi carrito</h1>

      {items.length === 0 && <p>Carrito vac√≠o</p>}

      {items.map(item => (
        <div key={item.id_producto} style={{ border: "1px solid #ddd", padding: "10px", marginBottom: "10px" }}>
          <h3>{item.nombre}</h3>
          <p>Precio: S/. {item.precio}</p>
          <p>Cantidad: {item.cantidad}</p>

          <button onClick={() => actualizarCantidad(item.id_producto, item.cantidad + 1)}>+</button>
          <button onClick={() => actualizarCantidad(item.id_producto, item.cantidad - 1)}>-</button>

          <button onClick={() => eliminarItem(item.id_producto)} style={{ marginLeft: "10px" }}>
            Eliminar
          </button>
        </div>
      ))}

       <button
        onClick={() => navigate("/pedido")}
        style={{ marginTop: "20px" }}
        >
          Generar Pedido
        </button>

    </div>
  );
}
