import { useState, useEffect } from "react";


export default function Pago({ id_carrito, id_direccion }) {
  const [totalDeuda, setTotalDeuda] = useState(0);
  const [monto, setMonto] = useState("");
  const [deudaPedido, setDeudaPedido] = useState(0);
  const [metodo, setMetodo] = useState("");
  const [tarjeta, setTarjeta] = useState({
    numero: "",
    nombre: "",
    correo: "",
    fecha: "",
    cvv: ""
  });
  const [errores, setErrores] = useState({});

  useEffect(() => {
    if (!id_carrito) return;
    cargarDeuda();
  }, [id_carrito]);

  const cargarDeuda = async () => {
    try {
      const r = await fetch(`http://localhost:3001/deuda?id_carrito=${id_carrito}`);
      const data = await r.json();
      setTotalDeuda(data.deuda ?? 0);
    } catch (err) {
      console.error("Error cargando deuda:", err);
    }
  };

  const cargarDeudaPedido = async (id_pedido) => {
  try {
    const r = await fetch(`http://localhost:3001/pagos/deuda/${id_pedido}`);
    const data = await r.json();
    setDeudaPedido(data.deuda ?? 0);
  } catch (err) {
    console.error("Error cargando deuda del pedido:", err);
  }
};


  const validar = () => {
    let e = {};
    const val = Number(monto);
    if (!val || val <= 0) e.monto = "Debe ingresar un monto válido";
    if (val > totalDeuda) e.monto = "No puedes pagar más de la deuda";
    if (metodo === "tarjeta") {
      if ((tarjeta.numero || "").length !== 16) e.numero = "Tarjeta inválida";
      if ((tarjeta.nombre || "").length < 3) e.nombre = "Nombre inválido";
      if (!((tarjeta.correo || "").includes("@"))) e.correo = "Correo inválido";
      if (!/^[0-9]{2}\/[0-9]{2}$/.test(tarjeta.fecha)) e.fecha = "Formato MM/YY";
      if ((tarjeta.cvv || "").length !== 3) e.cvv = "CVV inválido";
    }
    setErrores(e);
    return Object.keys(e).length === 0;
  };

  const procesarPago = async () => {
  if (!validar()) return;

  try {
    let pedidoId = idPedido;

    // Si aun no existe pedido → crearlo
    if (!pedidoId) {
      const id_estado = Number(monto) === totalDeuda ? 5 : 4;
      const fecha_pedido = new Date().toISOString().slice(0, 10);

      const resPedido = await fetch("http://localhost:3001/pedido/crear", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          id_carrito,
          id_direccion,
          fecha_pedido,
          id_estado
        })
      });

      const jsonPedido = await resPedido.json();
      if (!resPedido.ok) {
        alert(jsonPedido.error || "Error creando pedido");
        return;
      }

      pedidoId = jsonPedido.id_pedido;
      setIdPedido(pedidoId);
    }

    // Registrar pago
    const resPago = await fetch("http://localhost:3001/pagos/registrar", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        id_pedido: pedidoId,
        metodo_pago: metodo,
        monto: Number(monto)
      })
    });

    const jsonPago = await resPago.json();

    if (!resPago.ok) {
      alert(jsonPago.error || "Error registrando pago");
      return;
    }

    alert("Pago registrado correctamente");

    // Deuda restante
    await cargarDeudaPedido(pedidoId);

    setMonto("");
    setMetodo("");
    
  } catch (err) {
    console.error("Error procesando pago:", err);
    alert("Error procesando pago");
  }
};



  return (
    <div className="p-6 max-w-md mx-auto space-y-4">
      <h2 className="text-xl font-bold">Deuda del Pedido (carrito)</h2>
      <div className="p-4 border rounded-xl text-center">
        <p className="text-lg"><strong>Deuda total:</strong><br/>S/ {totalDeuda}</p>
      </div>

      <input
        className="p-2 border rounded w-full"
        placeholder="Ingrese monto a pagar"
        type="number"
        value={monto}
        onChange={(e) => setMonto(e.target.value)}
      />
      {errores.monto && <p className="text-red-500 text-sm">{errores.monto}</p>}

      <div className="space-y-2">
        <label>
          <input type="radio" name="mp" value="paypal" onChange={() => setMetodo("paypal")}/> PayPal
        </label>
        <label>
          <input type="radio" name="mp" value="tarjeta" onChange={() => setMetodo("tarjeta")}/> Tarjeta
        </label>
      </div>

      {metodo === "tarjeta" && (
        <div className="grid gap-2 p-4 border rounded-xl">
          <input placeholder="Número de tarjeta" maxLength={16} value={tarjeta.numero} onChange={(e) => setTarjeta({...tarjeta, numero: e.target.value})}/>
          {errores.numero && <p className="text-red-500 text-sm">{errores.numero}</p>}
          <input placeholder="Nombre" value={tarjeta.nombre} onChange={(e) => setTarjeta({...tarjeta, nombre: e.target.value})}/>
          {errores.nombre && <p className="text-red-500 text-sm">{errores.nombre}</p>}
          <input placeholder="Correo" value={tarjeta.correo} onChange={(e) => setTarjeta({...tarjeta, correo: e.target.value})}/>
          {errores.correo && <p className="text-red-500 text-sm">{errores.correo}</p>}
          <input placeholder="MM/YY" maxLength={5} value={tarjeta.fecha} onChange={(e) => setTarjeta({...tarjeta, fecha: e.target.value})}/>
          {errores.fecha && <p className="text-red-500 text-sm">{errores.fecha}</p>}
          <input placeholder="CVV" maxLength={3} value={tarjeta.cvv} onChange={(e) => setTarjeta({...tarjeta, cvv: e.target.value})}/>
          {errores.cvv && <p className="text-red-500 text-sm">{errores.cvv}</p>}
        </div>
      )}

      <button className="p-3 bg-blue-600 text-white rounded-xl w-full" onClick={procesarPago}>
        Generar pedido y pagar
      </button>
    </div>
  );
}
