import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import './header.css';
export default function Header() {
  const [rol, setRol] = useState(localStorage.getItem("rol"));
  const navigate = useNavigate();

  useEffect(() => {
    const handleStorageChange = () => {
      setRol(localStorage.getItem("rol"));
    };

    window.addEventListener("storage", handleStorageChange);
    return () => window.removeEventListener("storage", handleStorageChange);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("rol");
    localStorage.removeItem("id_usuario");

    setRol(null);
    navigate("/");
  };
  const handleClick = (path) => {
    if (rol === "cliente") {
      navigate(path);
    } else {
      // No hace nada si no eres cliente
      console.log("Acceso restringido: solo clientes pueden usar este botÃ³n");
    }
  };

  return (
    <header className="box">
      <div className="logo">
        <img src="/header/logo.png" alt="logo" />
        <span>LA TIENDA DEL BICHO</span>
      </div>
      <div className="BusquedaBar">
        <button onClick={() => handleClick("/productos")}>
          <img src="/header/icon-search.png" alt="buscar" />
        </button>
      </div>
      <div className="iconos-header">
        <div className="iconos">
           <button className="btn-im" onClick={() => handleClick("/carrito")}>
              <img src="/header/carrito.png" alt="carrito" />
            </button>
            <button className="btn-im" onClick={() => handleClick("/profile")}>
              <img src="/header/icono-user.png" alt="user" />
          </button>
            {/* ðŸ”¹ Logout solo si hay rol */}
              {rol && (
                <button className="btn-im" onClick={handleLogout} style={{ marginLeft: "10px" }}>
                  <img src="/header/out.png" alt="carrito" />
                </button>
              )}
        </div>
      </div>
    </header>
  );
}
