import { useState, useEffect } from "react";

export default function Pago({ id_pedido }) {
  const [totalDeuda, setTotalDeuda] = useState(0);
  const [metodo, setMetodo] = useState("");
  const [tarjeta, setTarjeta] = useState({
    numero: "",
    nombre: "",
    correo: "",
    fecha: "",
    cvv: "",
    monto: ""
  });
  const [errores, setErrores] = useState({});

  // =============================
  //   CARGAR DEUDA DESDE BACKEND
  // =============================
  useEffect(() => {
    cargarDeuda();
  }, []);

  const cargarDeuda = async () => {
    const r = await fetch(`http://localhost:3001/deuda?id_pedido=${id_pedido}`);
    const data = await r.json();
    setTotalDeuda(data.deuda);
  };

  // =============================
  //     VALIDAR MONTOS FINALES
  // =============================
  const validarMontoFinal = (valor) => {
    const n = Number(valor);

    if (isNaN(n)) return "Debes ingresar un número.";
    if (n <= 0) return "El pago debe ser mayor a 0.";
    if (n > totalDeuda) return "No puedes pagar más de lo que debes.";

    return null;
  };

  // =============================
  //   VALIDAR DATOS DE TARJETA
  // =============================
  const validarTarjeta = () => {
    let e = {};

    // Validación del MONTO — usa la lógica de deuda
    const montoError = validarMontoFinal(tarjeta.monto);
    if (montoError) e.monto = montoError;

    if (tarjeta.numero.length !== 16) e.numero = "La tarjeta debe tener 16 dígitos";
    if (tarjeta.nombre.length < 3) e.nombre = "Nombre inválido";
    if (!tarjeta.correo.includes("@")) e.correo = "Correo inválido";
    if (!/^[0-9]{2}\/[0-9]{2}$/.test(tarjeta.fecha)) e.fecha = "Formato MM/YY";
    if (tarjeta.cvv.length !== 3) e.cvv = "CVV debe tener 3 dígitos";

    setErrores(e);
    return Object.keys(e).length === 0;
  };

  // =============================
  //        PROCESAR PAGO
  // =============================
  const pagar = async () => {
    if (metodo === "") {
      alert("Selecciona un método de pago.");
      return;
    }

    if (metodo === "tarjeta" && !validarTarjeta()) return;

    const montoFinal = metodo === "tarjeta"
      ? tarjeta.monto
      : totalDeuda; // PayPal paga todo

    // Validación final
    const err = validarMontoFinal(montoFinal);
    if (err) {
      alert(err);
      return;
    }

    // =============================
    //  ENVIAR PAGO AL BACKEND
    // =============================
    const body = {
      metodo_pago: metodo,
      monto: montoFinal,
      id_pedido
    };

    const r = await fetch("http://localhost:3001/pagos/registrar", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(body)
    });

    const data = await r.json();
    if (!r.ok) {
      alert(data.error || "Error al procesar pago");
      return;
    }

    alert("Pago registrado correctamente");

    // TODO: luego agregas aquí:
    // - generar boleta
    // - limpiar carrito
    // - actualizar pedido
  };

  // =============================
  //       RENDER DEL FORM
  // =============================
  return (
    <div className="p-6 max-w-md mx-auto space-y-5">
      <h2 className="text-xl font-bold text-center">Detalle del Pago</h2>

      <p className="text-lg text-center">
        <strong>Deuda total: </strong> S/ {totalDeuda}
      </p>

      {/* Métodos de pago */}
      <div className="space-y-2">
        <label className="flex items-center gap-2">
          <input type="radio" name="mp" value="paypal"
            onChange={() => setMetodo("paypal")} />
          PayPal (paga todo)
        </label>

        <label className="flex items-center gap-2">
          <input type="radio" name="mp" value="tarjeta"
            onChange={() => setMetodo("tarjeta")} />
          Tarjeta de crédito
        </label>
      </div>

      {/* Form tarjeta */}
      {metodo === "tarjeta" && (
        <div className="grid gap-3 p-4 border rounded-xl">
          <input className="p-2 border rounded" placeholder="Número de tarjeta"
            value={tarjeta.numero}
            maxLength={16}
            onChange={(e) => setTarjeta({ ...tarjeta, numero: e.target.value })} />
          {errores.numero && <p className="text-red-500 text-sm">{errores.numero}</p>}

          <input className="p-2 border rounded" placeholder="Nombre"
            value={tarjeta.nombre}
            onChange={(e) => setTarjeta({ ...tarjeta, nombre: e.target.value })} />
          {errores.nombre && <p className="text-red-500 text-sm">{errores.nombre}</p>}

          <input className="p-2 border rounded" placeholder="Correo"
            value={tarjeta.correo}
            onChange={(e) => setTarjeta({ ...tarjeta, correo: e.target.value })} />
          {errores.correo && <p className="text-red-500 text-sm">{errores.correo}</p>}

          <input className="p-2 border rounded" placeholder="MM/YY"
            value={tarjeta.fecha}
            maxLength={5}
            onChange={(e) => setTarjeta({ ...tarjeta, fecha: e.target.value })} />
          {errores.fecha && <p className="text-red-500 text-sm">{errores.fecha}</p>}

          <input className="p-2 border rounded" placeholder="CVV" maxLength={3}
            value={tarjeta.cvv}
            onChange={(e) => setTarjeta({ ...tarjeta, cvv: e.target.value })} />
          {errores.cvv && <p className="text-red-500 text-sm">{errores.cvv}</p>}

          <input className="p-2 border rounded" placeholder="Monto a pagar"
            value={tarjeta.monto}
            onChange={(e) => setTarjeta({ ...tarjeta, monto: e.target.value })} />
          {errores.monto && <p className="text-red-500 text-sm">{errores.monto}</p>}
        </div>
      )}

      {metodo && (
        <button className="p-3 bg-blue-600 text-white rounded-xl w-full"
          onClick={pagar}>
          Generar pago
        </button>
      )}
    </div>
  );
}
