import React, { useState } from "react";

export default function Pedido() {
  // DIRECCIONES DEL USUARIO (SIN BACKEND → HARDCODEADO)
  const [direcciones, setDirecciones] = useState([
    {
      id: 1,
      distrito: "Miraflores",
      calle: "Av. Larco 123",
      referencia: "Frente al Parque Kennedy",
      codigo_postal: "15074",
      pais: "Perú",
      ciudad: "Lima",
    }
  ]);

  const [mostrarAgregar, setMostrarAgregar] = useState(false);

  // FORM NUEVA DIRECCION
  const [form, setForm] = useState({
    distrito: "",
    calle: "",
    referencia: "",
    codigo_postal: "",
    id_pais: "",
    id_ciudad: "",
  });

  const paises = [
    { id_pais: 1, nombre: "Perú" },
    { id_pais: 2, nombre: "Chile" },
  ];

  const ciudades = [
    { id_ciudad: 1, nombre: "Lima" },
    { id_ciudad: 2, nombre: "Cusco" },
  ];

  // MÉTODO DE PAGO
  const [metodoPago, setMetodoPago] = useState("");

  // MANEJADORES
  const cambiarValor = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const agregarDireccion = (e) => {
    e.preventDefault();

    const nueva = {
      id: direcciones.length + 1,
      distrito: form.distrito,
      calle: form.calle,
      referencia: form.referencia,
      codigo_postal: form.codigo_postal,
      pais: paises.find(p => p.id_pais == form.id_pais)?.nombre || "",
      ciudad: ciudades.find(c => c.id_ciudad == form.id_ciudad)?.nombre || "",
    };

    setDirecciones([...direcciones, nueva]);
    setMostrarAgregar(false);

    // limpiar form
    setForm({
      distrito: "",
      calle: "",
      referencia: "",
      codigo_postal: "",
      id_pais: "",
      id_ciudad: "",
    });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Generar Pedido</h2>

      {/* LISTA DE DIRECCIONES */}
      <h3>Elige una dirección</h3>
      {direcciones.map((d) => (
        <div key={d.id} style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "10px" }}>
          <strong>{d.calle}</strong> — {d.distrito}, {d.ciudad}, {d.pais}
          <br />
          <em>{d.referencia}</em>
        </div>
      ))}

      <button onClick={() => setMostrarAgregar(!mostrarAgregar)}>
        {mostrarAgregar ? "Cancelar" : "Agregar nueva dirección"}
      </button>

      {/* FORMULARIO NUEVA DIRECCIÓN */}
      {mostrarAgregar && (
        <div style={{ marginTop: "20px" }}>
          <h3>Agregar nueva dirección</h3>

          <form onSubmit={agregarDireccion}>
            <input
              type="text"
              name="distrito"
              placeholder="Distrito"
              value={form.distrito}
              onChange={cambiarValor}
              required
            /><br/>

            <input
              type="text"
              name="calle"
              placeholder="Calle"
              value={form.calle}
              onChange={cambiarValor}
              required
            /><br/>

            <input
              type="text"
              name="referencia"
              placeholder="Referencia"
              value={form.referencia}
              onChange={cambiarValor}
              required
            /><br/>

            <input
              type="text"
              name="codigo_postal"
              placeholder="Código Postal"
              value={form.codigo_postal}
              onChange={cambiarValor}
              required
            /><br/>

            <select name="id_pais" value={form.id_pais} onChange={cambiarValor} required>
              <option value="">Seleccione un país</option>
              {paises.map((p) => (
                <option key={p.id_pais} value={p.id_pais}>
                  {p.nombre}
                </option>
              ))}
            </select><br/>

            <select name="id_ciudad" value={form.id_ciudad} onChange={cambiarValor} required>
              <option value="">Seleccione una ciudad</option>
              {ciudades.map((c) => (
                <option key={c.id_ciudad} value={c.id_ciudad}>
                  {c.nombre}
                </option>
              ))}
            </select><br/>

            <button type="submit">Guardar dirección</button>
          </form>
        </div>
      )}

      {/* MÉTODO DE PAGO */}
      <h3 style={{ marginTop: "30px" }}>Método de pago</h3>

      <label>
        <input
          type="radio"
          name="pago"
          value="tarjeta"
          onChange={(e) => setMetodoPago(e.target.value)}
        />
        Tarjeta
      </label>
      <br/>
      <label>
        <input
          type="radio"
          name="pago"
          value="paypal"
          onChange={(e) => setMetodoPago(e.target.value)}
        />
        PayPal
      </label>

      <br/><br/>
      <button disabled={!metodoPago}>Continuar</button>
    </div>
  );
}
