import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
export default function Deuda() {
    {Array.isArray(deudas) && deudas.length === 0 && (
  <p>No tienes pedidos pendientes de pago.</p>
)}

{Array.isArray(deudas) &&
  deudas.map((d) => (
    <div key={d.id_pedido}>
      error
    </div>
))}
  const [deudas, setDeudas] = useState([]);
  const navigate = useNavigate();
  const id_cliente = localStorage.getItem("id_usuario");

  useEffect(() => {
    cargarDeudas();
  }, []);

  const cargarDeudas = async () => {
    try {
      const r = await fetch(`http://localhost:3001/deudas/${id_cliente}`);
      const data = await r.json();
      setDeudas(data);
    } catch (err) {
      console.error("Error cargando deudas:", err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Pedidos pendientes (Deudas)</h2>

      {deudas.length === 0 ? (
        <p>No tienes pedidos pendientes de pago.</p>
      ) : (
        deudas.map((d) => (
          <div
            key={d.id_pedido}
            style={{
              border: "1px solid #ccc",
              padding: "12px",
              marginTop: "10px",
              borderRadius: "6px"
            }}
          >
            <h3>Pedido #{d.id_pedido}</h3>
            <p><strong>Fecha:</strong> {d.fecha_pedido}</p>
            <p><strong>Total:</strong> S/. {d.total}</p>
            <p><strong>Pagado:</strong> S/. {d.pagado}</p>
            <p><strong>Deuda restante:</strong> S/. {d.deuda}</p>

            <button
              style={{ marginTop: "10px" }}
              onClick={() =>
                navigate("/pago", {
                  state: { id_pedido: d.id_pedido, deuda: d.deuda },
                })
              }
            >
              Pagar deuda
            </button>
          </div>
        ))
      )}
    </div>
  );
}
