import { useState, useEffect } from "react";

export default function Direcciones({ id_cliente }) {

  const [direcciones, setDirecciones] = useState([]);
  const [paises, setPaises] = useState([]);
  const [ciudades, setCiudades] = useState([]);

  const [mostrarAgregar, setMostrarAgregar] = useState(false);
  const [direccionSeleccionada, setDireccionSeleccionada] = useState(null);

  const [form, setForm] = useState({
    distrito: "",
    calle: "",
    referencia: "",
    codigo_postal: "",
    id_pais: "",
    id_ciudad: "",
    id_cliente: id_cliente
  });

  //  Solo cargamos direcciones cuando id_cliente existe
  useEffect(() => {
    if (!id_cliente) return;

    cargarDirecciones();
  }, [id_cliente]);

  const cargarDirecciones = async () => {
    const r = await fetch(`/direccion?id_cliente=${id_cliente}`);
    const data = await r.json();
    setDirecciones(data);
  };

  const cargarPaises = async () => {
    const r = await fetch("/paises");
    const data = await r.json();
    setPaises(data);
  };

  const cargarCiudades = async (id_pais) => {
    const r = await fetch(`/ciudades?id_pais=${id_pais}`);
    const data = await r.json();
    setCiudades(data);
  };

  const cambiarValor = (e) => {
    const { name, value } = e.target;

    setForm({
      ...form,
      [name]: value
    });

    if (name === "id_pais") {
      cargarCiudades(value);
    }
  };

  const agregarDireccion = async (e) => {
    e.preventDefault();

    const r = await fetch("/direccionAgregar", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form)
    });

    const data = await r.json();
    alert(data.mensaje);

    cargarDirecciones();
    setMostrarAgregar(false);

    // Reset form
    setForm({
      distrito: "",
      calle: "",
      referencia: "",
      codigo_postal: "",
      id_pais: "",
      id_ciudad: "",
      id_cliente
    });
  };

  return (
    <div style={{ marginTop: "30px" }}>

      <button
        onClick={() => {
          setMostrarAgregar(!mostrarAgregar);
          if (!mostrarAgregar) cargarPaises();
        }}
      >
        {mostrarAgregar ? "Cerrar formulario" : "Agregar direcci贸n"}
      </button>

      <h3 style={{ marginTop: "20px" }}>Mis direcciones</h3>
      {direcciones.length === 0 ? (
        <p>No tienes direcciones registradas</p>
      ) : (
        <ul>
          {direcciones.map((d, i) => (
            <li key={i}>
              <label style={{ cursor: "pointer" }}>
                <input
                  type="radio"
                  name="seleccionDireccion"
                  value={i}
                  checked={direccionSeleccionada === i}
                  onChange={() => setDireccionSeleccionada(i)}
                />
                {" "}
                <strong>{d.calle}</strong> - {d.distrito}, {d.ciudad} ({d.nombre})
              </label>
            </li>
          ))}
        </ul>
      )}

      {direccionSeleccionada !== null && (
        <p style={{ color: "green" }}>
          Direcci贸n seleccionada: {direcciones[direccionSeleccionada].calle}
        </p>
      )}

      {mostrarAgregar && (
        <div style={{ marginTop: "20px" }}>
          <h3>Agregar nueva direcci贸n</h3>

          <form onSubmit={agregarDireccion}>

            <input
              type="text"
              name="distrito"
              placeholder="Distrito"
              value={form.distrito}
              onChange={cambiarValor}
              required
            /><br/>

            <input
              type="text"
              name="calle"
              placeholder="Calle"
              value={form.calle}
              onChange={cambiarValor}
              required
            /><br/>

            <input
              type="text"
              name="referencia"
              placeholder="Referencia"
              value={form.referencia}
              onChange={cambiarValor}
              required
            /><br/>

            <input
              type="text"
              name="codigo_postal"
              placeholder="C贸digo Postal"
              value={form.codigo_postal}
              onChange={cambiarValor}
              required
            /><br/>

            <select
              name="id_pais"
              value={form.id_pais}
              onChange={cambiarValor}
              required
            >
              <option value="">Seleccione un pa铆s</option>
              {paises.map((p) => (
                <option key={p.id_pais} value={p.id_pais}>
                  {p.nombre}
                </option>
              ))}
            </select><br/>

            <select
              name="id_ciudad"
              value={form.id_ciudad}
              onChange={cambiarValor}
              required
            >
              <option value="">Seleccione una ciudad</option>
              {ciudades.map((c) => (
                <option key={c.id_ciudad} value={c.id_ciudad}>
                  {c.nombre}
                </option>
              ))}
            </select><br/>

            <button type="submit">Guardar</button>
          </form>
        </div>
      )}

    </div>
  );
}
