import { useEffect, useState } from "react";

export default function Header() {
  const navigate = useNavigate();
  const [usuario, setUsuario] = useState(false);

  useEffect(() => {
    const u = localStorage.getItem("usuario");
    if (u) setUsuario(true);
  }, []);

