import React, { useEffect, useState } from "react";

export default function Deudas() {
  const id_cliente = localStorage.getItem("id_usuario");
  const [deudas, setDeudas] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:3000/deudas/${id_cliente}`)
      .then(res => res.json())
      .then(data => {
        console.log("RESPUESTA DE BACKEND DEUDAS:", data);

        // Si data.deudas existe → úsalo
        if (Array.isArray(data)) {
          setDeudas(data);
        } else if (Array.isArray(data.deudas)) {
          setDeudas(data.deudas);
        } else {
          setDeudas([]); // evita crash
        }
      })
      .catch(err => console.error(err));
  }, [id_cliente]);

  if (!Array.isArray(deudas))
    return <div>No se pudieron cargar las deudas.</div>;

  return (
    <div>
      <h2>Deudas</h2>

      {deudas.length === 0 ? (
        <p>No tienes deudas pendientes</p>
      ) : (
        deudas.map((d) => (
          <div key={d.id_pedido} className="deuda-card">
            <p>Pedido #{d.id_pedido}</p>
            <p>Total: S/ {d.total}</p>
            <p>Deuda pendiente: S/ {d.deuda}</p>

            <button
              onClick={() => {
                localStorage.setItem("pedido_pendiente", d.id_pedido);
                window.location.href = "/pagar-deuda";
              }}
            >
              Pagar
            </button>
          </div>
        ))
      )}
    </div>
  );
}
