import React, { useState, useEffect } from "react";

const API = "http://localhost:3001";

export default function GestionListas() {
  const [tipo, setTipo] = useState("");
  const [items, setItems] = useState([]);
  const [nuevo, setNuevo] = useState("");
  const [pais, setPais] = useState("");
  const [loading, setLoading] = useState(false);

  // ================================
  // Endpoints por cada tipo
  // ================================
  const endpoints = {
    categoria: {
      get: "/categorias",
      post: "/categorias",
      delete: "/categorias/"
    },
    equipo: {
      get: "/equipos",
      post: "/equipos",
      delete: "/equipos/"
    },
    marca: {
      get: "/marcas",
      post: "/marcas",
      delete: "/marcas/"
    },
    competencia: {
      get: "/competencias",
      post: "/competencias",
      delete: "/competencias/"
    }
  };

  // ================================
  // Cargar lista
  // ================================
  const cargarDatos = async () => {
    if (!tipo) return;
    setLoading(true);

    try {
      const res = await fetch(API + endpoints[tipo].get);
      const data = await res.json();
      setItems(data);
    } catch (err) {
      console.error(err);
      alert("Error cargando datos.");
    }

    setLoading(false);
  };

  useEffect(() => {
    cargarDatos();
  }, [tipo]);


  // ================================
  // Agregar elemento
  // ================================
  const agregar = async () => {
    if (!nuevo) {
      alert("Ingresa un nombre");
      return;
    }

    try {
      let body = { nombre: nuevo };

      if (tipo === "equipo" || tipo === "marca") {
        if (!pais.trim()) return alert("Debes ingresar el país.");
        body.pais = pais;
      }

      await fetch(API + endpoints[tipo].post, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body)
      });

      alert("Agregado correctamente");
      setNuevo("");
      setPais("");
      cargarDatos();
    } catch (err) {
      console.error(err);
      alert("Error agregando");
    }
  };


  // ================================
  // Eliminar elemento
  // ================================
  const eliminar = async (id) => {
    if (!window.confirm("¿Seguro que deseas eliminar?")) return;

    try {
      await fetch(API + endpoints[tipo].delete + id, { method: "DELETE" });
      alert("Eliminado correctamente");
      cargarDatos();
    } catch (err) {
      console.error(err);
      alert("Error eliminando");
    }
  };


  return (
    <div style={{ padding: 20 }}>
      <h2>Gestión de Listas</h2>

      {/* BOTONES PRINCIPALES */}
      <div style={{ marginBottom: 20 }}>
        <button onClick={() => setTipo("categoria")}>Categorías</button>
        <button onClick={() => setTipo("equipo")}>Equipos</button>
        <button onClick={() => setTipo("marca")}>Marcas</button>
        <button onClick={() => setTipo("competencia")}>Competencias</button>
      </div>

      {tipo && (
        <h3>Administrando: <strong>{tipo.toUpperCase()}</strong></h3>
      )}

      {/* FORMULARIO */}
      {tipo && (
        <div style={{ marginBottom: 20 }}>
          <input
            type="text"
            placeholder="Nuevo nombre"
            value={nuevo}
            onChange={(e) => setNuevo(e.target.value)}
          />

          {(tipo === "equipo" || tipo === "marca") && (
            <input
              type="text"
              placeholder="País"
              value={pais}
              onChange={(e) => setPais(e.target.value)}
              style={{ marginLeft: 10 }}
            />
          )}

          <button onClick={agregar} style={{ marginLeft: 10 }}>
            Agregar
          </button>
        </div>
      )}

      {/* LISTADO */}
      {loading ? (
        <p>Cargando...</p>
      ) : (
        <ul>
          {items.map((item) => (
            <li key={item.id} style={{ marginBottom: 10 }}>
              <strong>{item.nombre}</strong>

              {item.pais && (
                <span> — {item.pais}</span>
              )}

              <button
                onClick={() => eliminar(item.id)}
                style={{ marginLeft: 15 }}
              >
                Eliminar
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
