import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import './header.css';

export default function Header() {
  const [rol, setRol] = useState(localStorage.getItem("rol"));
  const navigate = useNavigate();

  useEffect(() => {
    const handleStorageChange = () => {
      setRol(localStorage.getItem("rol"));
    };
    window.addEventListener("storage", handleStorageChange);
    return () => window.removeEventListener("storage", handleStorageChange);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("rol");
    localStorage.removeItem("id_usuario");
    localStorage.removeItem("id_empleado");
    setRol(null);
    navigate("/");
  };

  return (
    <header className="box">
      {/* Logo */}
      <div className="logo">
        <img src="/header/logo.png" alt="logo" />
        <span>LA TIENDA DEL BICHO</span>
      </div>

      {/* Barra de búsqueda */}
      <div className="BusquedaBar">
        <input type="text" placeholder="Buscar..." />
        <button>
          <img src="/header/icon-search.png" alt="buscar" />
        </button>
      </div>

      {/* Iconos y menú según rol */}
      <div className="iconos-header">
        <div className="iconos">
          {rol === "cliente" && (
            <>
              <button className="btn-im" onClick={() => navigate("/carrito")}>
                <img src="/header/carrito.png" alt="carrito" />
              </button>
              <button className="btn-im" onClick={() => navigate("/profile")}>
                <img src="/header/icono-user.png" alt="user" />
              </button>
              <button className="btn-im" onClick={() => navigate("/responder-encuestas")}>
                Encuestas
              </button>
              <button className="btn-im" onClick={handleLogout}>
                Log out
              </button>
            </>
          )}

          {rol === "empleado" && (
            <>
              <button className="btn-im" onClick={() => navigate("/encuestas")}>
                Encuestas
              </button>
              <button className="btn-im" onClick={() => navigate("/productos/agregar")}>
                Editar Productos
              </button>
              <button className="btn-im" onClick={() => navigate("/modificar_item")}>
                Modificar items
              </button>
              <button className="btn-im" onClick={handleLogout}>
                Log out
              </button>
            </>
          )}

          {!rol && (
            <>
              <button className="btn-im" onClick={() => navigate("/productos")}>
                Productos
              </button>
              <button className="btn-im" onClick={() => navigate("/login-user")}>
                <img src="/header/icono-user.png" alt="login" />
              </button>
            </>
          )}

          {/* Menú hamburguesa */}
          <div className="menu-icon">
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>
      </div>
    </header>
  );
}
