import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function ProductoDetalles() {
  const { id } = useParams();
  const [producto, setProducto] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3001/producto/${id}`)
      .then(res => res.json())
      .then(data => setProducto(data))
      .catch(err => console.error(err));
  }, [id]);

  if (!producto) return <p>Cargando...</p>;

  return (
    <div>
      <h1>{producto.nombre_producto}</h1>
      <img src={producto.imagen} width={250} />
      <p><strong>Precio:</strong> {producto.precio}</p>
      <p><strong>Descripcion:</strong> {producto.caracteristica}</p>
      <p><strong>Categor√≠a:</strong> {producto.categoria}</p>
      <p><strong>Competencia:</strong> {producto.competencia}</p>
    </div>
  );
}
