import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Login({ tipo }) {
  const [datos, setDatos] = useState({ correo: "", password: "" });
  const navigate = useNavigate();

  const actualizar = (e) => {
    setDatos({ ...datos, [e.target.name]: e.target.value });
  };

  const enviarLogin = (e) => {
    e.preventDefault();

    const url =
      tipo === "user"
        ? "http://localhost:3001/login/user"
        : "http://localhost:3001/login/employee";

    fetch(url, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(datos),
    })
      .then((res) => res.json())
      .then((msg) => {
        alert(msg.message);

        if (tipo === "user") {
          navigate("/productos");
        } 
        else {
          // si es empleado → después definimos a dónde
          navigate("/panel-empleado");
        }
      })
      .catch((err) => console.error("ERROR FRONT:", err));
  };

  return (
    <div>
      <h2>Login {tipo === "user" ? "Cliente" : "Empleado"}</h2>
      <form onSubmit={enviarLogin}>
        <input
          type="text"
          name="correo"
          placeholder="Correo"
          value={datos.correo}
          onChange={actualizar}
        />
        <input
          type="password"
          name="password"
          placeholder="Contraseña"
          value={datos.contrasena}
          onChange={actualizar}
        />
        <button type="submit">Ingresar</button>
      </form>

      {tipo === "user" && (
        <button onClick={() => navigate("/registro")}>Registrate</button>
      )}
    </div>
  );
}
